import axios from "axios";
import React, { useState,useId,useEffect} from "react";
import { useNavigate } from "react-router";
import { Link } from "react-router-dom";


const Add = () => {
  const[name,setName]=useState("");
  const[email,setEmail]=useState("");
  const[Phone,setPhone]=useState("");
  const[Pan,setPan]=useState("");
  const[Client,setClient]=useState("");
  const[Entity,setEntity]=useState("");
  const [clientOptions, setClientOptions] = useState([]);
  const [entityOptions, setEntityOptions] = useState([]);
  const history = useNavigate();
  const idprefix = useId();
  const uniqueId = `${idprefix}-${name}`;
  const header={"Access-Control-Allow-Origin":"*"};
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("clicked");
    axios
      .post("https://66802d8556c2c76b495b61de.mockapi.io/xrud", {
        name: name,
        email: email,
        Phone:Phone,
        Pan:Pan,
        ClientGroup :Client,
        Entity:Entity,
        UniqueId:uniqueId,
      })
      .then(() => {
        history("/read");
      });
    };
    const handleChange = (e) => {
        const value = e.target.value;
        setState({
          ...state,
          [e.target.name]: value,
        });
      };
      const [state, setState] = useState({
       client: "",
       entity:'',
      });
      useEffect(() => {
        // Fetch client options
        const fetchClientOptions = async () => {
          try {
            const response = await axios.get('https://your-backend-endpoint/api/clients');
            setClientOptions(response.data); // Assuming response.data is an array of client options
          } catch (error) {
            console.error('Error fetching client data:', error);
          }
        };
    
        // Fetch entity options
        const fetchEntityOptions = async () => {
          try {
            const response = await axios.get('https://your-backend-endpoint/api/entities');
            setEntityOptions(response.data); // Assuming response.data is an array of entity options
          } catch (error) {
            console.error('Error fetching entity data:', error);
          }
        };
    
        fetchClientOptions();
        fetchEntityOptions();
      }, []);
    
      useEffect(() => {
        // Fetch data from the backend when the component mounts
        const fetchData = async () => {
          try {
            const response = await axios.get('');
            setOptions2(response.data); // Assuming response.data is an array of options
          } catch (error) {
            console.error('Error fetching data:', error);
          }
        };
    
        fetchData();
      }, []);
  return <>
  <h2 class="my-4 bg-violet-200 mx-8 w-24 h-6">Add clients</h2>
        <form class="mx-4">
        <div class="mb-3">

          <label for="exampleInputPassword1" class="form-label">
            Client Group:
          </label>
          <select required id="exampleInputPassword1" name="client" class="form-control" value={state.client} onChange={handleChange}>
          <option value="">Choose-ClientGroup</option>
            {options.map((option) => (
              <option key={option.id} value={option.id}>
                {option.name}
              </option>
                ))}
        </select>
        </div>
        <div class="mb-3">
        <div class="mb-3">
          <label for="exampleInputPassword1" class="form-label">
            Entity Type:
          </label>
          <select name="entity" required id="exampleInputPassword1" class="form-control" value={state.entity} onChange={handleChange}>
          <option2 value="">Choose-ClientGroup</option2>
            {options2.map((option) => (
              <option2 key={option2.id} value={option2.id}>
                {option2.name}
              </option2>
                ))}
        </select>
        </div>
        </div>
      <div className="mb-3">
        <label className="form-label mr-8">Name</label>
        <input type="text" className="form-control" onChange={(e)=>setName(e.target.value)}/>
       
      </div>
      <div className="mb-3">
        <label className="form-label mr-8">Phone</label>
        <input type="text" className="form-control" onChange={(e)=>setPhone(e.target.value)}/>
       
      </div>
      <div className="mb-3">
        <label className="form-label mr-8">Pan-no.</label>
        <input type="text" className="form-control" onChange={(e)=>setPan(e.target.value)}/>
       
      </div>
      <div classNameName="mb-3">
        <label for="exampleInputEmail1" className="form-label pr-8">Email</label>
          <input 
            type="email" className="form-control" aria-describedby="emailHelp"
            onChange={(e)=>setEmail(e.target.value)}/>
          </div>
      <div className="mb-3 form-check">
        <label className="form-check-label" for="exampleCheck1"></label>
      </div>
      <button type="submit" className="btn btn-primary"   onClick={handleSubmit}>Submit</button>
    </form>
  </>
}

export default Add
